/**
 * Normalizes form of the KRS number
 * @param {string} krs input string
 * @return {string} normalized number
 */
export function normalize(krs: string): string;
/**
 * Checks if specified KRS is valid
 * @param {string} krs input number
 * @return {boolean} true if number is valid
 */
export function isValid(krs: string): boolean;
